"use strict";
function isErrorLike(x) {
    return x && typeof x.message === "string";
}
function isErrorLikeWithStack(x) {
    return isErrorLike(x) && typeof x.stack === "string";
}
function stringifyErrorLike(errorLike) {
    if (isErrorLikeWithStack(errorLike)) {
        return errorLike.stack;
    }
    else if (isErrorLike(errorLike)) {
        return errorLike.message;
    }
    else {
        return String(errorLike);
    }
}
exports.stringifyErrorLike = stringifyErrorLike;
